void FCM_clk_pulse()
{

	//clk
	//Output: 1 -> A4
	trisa = trisa & 0xEF;
	if ((1))
		porta = (porta & 0xEF) | 0x10;
	else
		porta = porta & 0xEF;

	//clk
	//Output: 0 -> A4
	trisa = trisa & 0xEF;
	if ((0))
		porta = (porta & 0xEF) | 0x10;
	else
		porta = porta & 0xEF;

}


void FCM_Scan_ShiftReg()
{

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP1=0; FCLV_LOOP1<8; FCLV_LOOP1++)
	{

		//Calculation
		//Calculation:
		//  dat = scan1 AND 1
		FCV_DAT = FCV_SCAN1 & 1;

		//Output
		//Output: dat -> A3
		trisa = trisa & 0xF7;
		if ((FCV_DAT))
			porta = (porta & 0xF7) | 0x08;
		else
			porta = porta & 0xF7;

		//Call Macro
		//Call Macro: clk_pulse()
		FCM_clk_pulse();

		//Calculation
		//Calculation:
		//  scan1 = scan1 >> 1
		FCV_SCAN1 = FCV_SCAN1 >> 1;


	}

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP2=0; FCLV_LOOP2<8; FCLV_LOOP2++)
	{

		//Calculation
		//Calculation:
		//  dat = scan2 AND 1
		FCV_DAT = FCV_SCAN2 & 1;

		//Output
		//Output: dat -> A3
		trisa = trisa & 0xF7;
		if ((FCV_DAT))
			porta = (porta & 0xF7) | 0x08;
		else
			porta = porta & 0xF7;

		//Call Macro
		//Call Macro: clk_pulse()
		FCM_clk_pulse();

		//Calculation
		//Calculation:
		//  scan2 = scan2 >> 1
		FCV_SCAN2 = FCV_SCAN2 >> 1;


	}

}


