void FCM_Interrupt()
{

	//Calculation
	//Calculation:
	//  temp_scan1 = scan1
	//  temp_scan2 = scan2
	FCV_TEMP_SCAN1 = FCV_SCAN1;
	FCV_TEMP_SCAN2 = FCV_SCAN2;

	//Switch
	//Switch: button_SCAN?
	switch (FCV_BUTTON_SCAN)
	{
		case 1:
		{
			//Call Macro
			//Call Macro: btn_scan1()
			FCM_btn_scan1();

			break;
		}
		case 2:
		{
			//Call Macro
			//Call Macro: btn_scan2()
			FCM_btn_scan2();

			break;
		}
		case 3:
		{
			//Call Macro
			//Call Macro: btn_scan3()
			FCM_btn_scan3();

			break;
		}
		// default:

	}

	//Calculation
	//Calculation:
	//  button_SCAN = button_SCAN + 1
	FCV_BUTTON_SCAN = FCV_BUTTON_SCAN + 1;

	//Decision
	//Decision: button_SCAN >= 4?
	if (FCV_BUTTON_SCAN >= 4)
	{

		//Calculation
		//Calculation:
		//  button_SCAN = 1
		FCV_BUTTON_SCAN = 1;

	// } else {

	}

	//Calculation
	//Calculation:
	//  scan1 = temp_scan1
	//  scan2 = temp_scan2
	FCV_SCAN1 = FCV_TEMP_SCAN1;
	FCV_SCAN2 = FCV_TEMP_SCAN2;

}


void FCM_btn_scan1()
{

	//Output
	//Output: 0b00000000 -> PORTB
	trisb = 0x00;
	portb = (0);

	//Calculation
	//Calculation:
	//  scan1 = 0x04
	//  scan2 = 0x00
	FCV_SCAN1 = 0x04;
	FCV_SCAN2 = 0x00;

	//Call Macro
	//Call Macro: Scan_ShiftReg()
	FCM_Scan_ShiftReg();

	//Input
	//Input: A0 -> switch4
	trisa = trisa | 0x01;
	FCV_SWITCH4 = ((porta & 0x01) == 0x01);

	//Input
	//Input: A1 -> switch5
	trisa = trisa | 0x02;
	FCV_SWITCH5 = ((porta & 0x02) == 0x02);

	//Decision
	//Decision: switch4 = 0?
	if (FCV_SWITCH4 == 0)
	{

		//Calculation
		//Calculation:
		//  btn_guest = 1
		FCV_BTN_GUEST = 1;

	} else {

		//Calculation
		//Calculation:
		//  btn_guest = 0
		//  toggle_guest = 0
		FCV_BTN_GUEST = 0;
		FCV_TOGGLE_GUEST = 0;

		//Decision
		//Decision: switch5 = 0?
		if (FCV_SWITCH5 == 0)
		{

			//Calculation
			//Calculation:
			//  btn_buzzer = 1
			FCV_BTN_BUZZER = 1;

		} else {

			//Calculation
			//Calculation:
			//  btn_buzzer = 0
			FCV_BTN_BUZZER = 0;

		}

	}

}


